CREATE PROCEDURE [dbo].[InsertarPersona]	@Nombre [nvarchar](50),	@Apellido [nvarchar](50),	@RUT [varchar](10),	@Tarjeta [varchar](20),	@TipoPase [nvarchar](50),	@Contrato [nvarchar](50) ,	@RazonSocial [nvarchar](50),	@FechaExpiracionTrabajador [varchar](8),	@MotivoRechazoTrabajador [nvarchar](4000),	@FechaExpiracionLicencia [varchar](8),	@MotivoRechazoLicencia [nvarchar](4000),	@Resultado [varchar] (10) OUTPUT,	@Mensaje [nvarchar](4000) OUTPUTASBEGIN TRY  	INSERT INTO [dbo].[Personas]	(		[Nombre],		[Apellido],		[RUT],		[Tarjeta],		[TipoPase],		[Contrato],		[RazonSocial],		[FechaExpiracionTrabajador],		[MotivoRechazoTrabajador],		[FechaExpiracionLicencia],		[MotivoRechazoLicencia]	)	VALUES	(		@Nombre,		@Apellido,		@RUT,		@Tarjeta,		@TipoPase,		@Contrato,		@RazonSocial,		@FechaExpiracionTrabajador,		@MotivoRechazoTrabajador,		@FechaExpiracionLicencia,		@MotivoRechazoLicencia	);	IF @@trancount > 0 COMMIT TRANSACTION;	SET @Resultado = 'EXITOSO';	SET @Mensaje = 'OK';END TRYBEGIN CATCH	IF @@trancount > 0 ROLLBACK TRANSACTION;	SET @Resultado = 'FALLIDO';	SET @Mensaje = ERROR_PROCEDURE() + ' '+ ERROR_MESSAGE(); END CATCH